//ES7+ React/Redux ... Search "React" in VSCode Extensions -> Type rafce in blank js file
import axios from 'axios';
import React, { useState, useEffect, Component } from 'react';
import './Initialtest.css'

const Initialtest = () => {
    /*
    state = {
        response: '',
        post: '',
        responseToPost: '',
    };*/ //instead of states in class components, we have hooks in functional components
    const [response, post, responseToPost] = useState('') //sets all var. to '', can use 0 or false, or etc

    /*when component first mounts onto website, run the following
    componentDidMount() {
        this.callApi()
            .then(res => this.setState({ response: res.express }))
            .catch(err => console.log(err));
    }*/
    //Also can useEffect() which does the same
    useEffect( () => {
        this.callApi()
            .then(res => this.setState({ response: res.express }))
            .catch(err => console.log(err));
    }, []);

    const callApi = async () => {
        return axios.get('/test/get')
            .then(res => {
                this.res = res.data
                if (res.status !== 200) throw Error(res.message);
                return res.data
            })
            .catch(err => {
                //console.log("HII: " + err)
                alert("ERROR:" + err)
            })
    };

    const handleSubmit = async e => {
        e.preventDefault();
        const response = await fetch('/test/post', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify({ post: this.state.post }),
        });
        const body = await response.text();

        this.setState({ responseToPost: body });
    };

    return (
        <div className="App">
        <header className="App-header">
            <p>
            CS180 Lab Connection Test
            </p>
        </header>
        <p>Server POST Message: {this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
            <p>
                <strong>Post to Server:</strong>
            </p>
            <input
                type="text"
                value={this.state.post} //<---? can work wthout
                //onChange = everytime add/remove a character
                onChange={e => this.setState({ post: e.target.value })}
            />
            <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p>
        </div>
    );
}

export default Initialtest
